import {Component, ElementRef, HostListener, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {AuthenticationService} from "../../services/authentication.service";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  isMobile = false;
  style: any;
  dropdownMenu: ElementRef;
  nom:String='';
  prenom:String='';


  private data: any=[];

  @HostListener('window:resize', ['$event'])
  onResize(event: any) {
    this.checkScreenSize();}

  constructor(private elementRef: ElementRef,private router: Router,private authenticationService : AuthenticationService) {
    this.dropdownMenu = this.elementRef.nativeElement.querySelector('.dropdown-menu');
  }

    ngOnInit() {
        this.nom=this.authenticationService.getUserData().nom;
     this.prenom=this.authenticationService.getUserData().prenom;

      this.dropdownMenu = this.elementRef.nativeElement.querySelector('.dropdown-menu');
      this.checkScreenSize();

      this.setActiveLink();
    }

  showDropdownMenu() {
    this.dropdownMenu.nativeElement.style.display = 'block';
  }
  private setActiveLink(): void {
    const currentUrl = this.router.url;

    const homeLink = document.querySelector('.A[href="/Home"]');
    const listUsersLink = document.querySelector('.A[href="/Home/listUsers"]');

    if (homeLink && currentUrl.includes('/Home')) {
      homeLink.classList.add('active');
      listUsersLink?.classList.remove('active'); // Remove 'active' class from the other link
    } else if (listUsersLink && currentUrl.includes('/Home/listUsers')) {
      listUsersLink.classList.add('active');
      homeLink?.classList.remove('active'); // Remove 'active' class from the other link
    }
    // Add similar conditions for other links...
  }
  showNewsDropdown = false;

  toggleDropdown() {
    this.showNewsDropdown = !this.showNewsDropdown;
  }

  checkScreenSize() {
      this.isMobile = window.innerWidth < 700;
    }

logout(){
    this.authenticationService.logout();

}






}
<div  style="display: inline-block; ">
<div class="sidebar">
  <img src="../../assets/GEOTRACKER.png" alt="notFound" class="geoTracker" />
  <div class="image-wrapper">
    <img src="../../assets/fedi.jpg" alt="notFound" class="IMG" />
    <div class="word-container">
      <h3 class="NAME">{{nom}} {{prenom}}</h3>
    </div>
    <div class="btn-group">
    <button style="background: transparent;" href="#" class="icon-button" >
      <fa-icon style="color: #b0c2cb ; position:relative ; left:40px  ;top:36px" [icon]="['fas', 'ellipsis-v']"></fa-icon>
    </button>
      <div class="dropdown-menu dropdown-menu-right">
        <button class="dropdown-item" type="button">Action</button>
        <button class="dropdown-item" type="button">Another action</button>
        <button class="dropdown-item" type="button">Something else here</button>
      </div>
    </div>
  </div>
  <br>
  <br>
  <br>
  <h5 style="color: white; font-family: Bahnschrift; font-size: 15px; position: relative; bottom: 4px; padding: 16px;">
    Navigation
  </h5>

  <a class="A" routerLink="/Home" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Acceuil</a>
  <a class="A" routerLink="/Home/listUsers" routerLinkActive="active">Utilisateurs</a>
  <a class="A" href="/Home/listTraces">Traces</a>
  <a class="A" href="/Home/listTraces">Brouillons</a>
  <a class="A" href="/Home/listTrajets">Trajets</a>
  <br>
  <br>
  <br>
<br>
  <br><br>
  <br>
  <a  class="A"  href="javascript:void(0);" (click)="logout()">Déconnexion</a>
</div>


  <div style="display: inline-block;" class="main-content">

    <div class="page-content">
      <div class="container-fluid">
        <router-outlet></router-outlet>
      </div>
    </div>
  </div>
</div>
